package basic;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		do{ 
			System.out.println("a: " + a);
			a++;
		}while (a <= 5);
		
		
		
	System.out.println("\n Char Exemple");
	 char b = 'A';
	 	do{ 
		 System.out.print(b + "  ");
		 b++;
	 	}while (b <= 'Z');
	
	 
	}

}
